import axios from 'axios'
import { useState } from 'react'
// import { useNavigate } from 'react-router-dom'

function NewPost() {
  const [file, setFile]= useState();
  const [caption, setCaption]=useState("")
  // const navigate = useNavigate()
  const submit= async(event)=>{
    event.preventDefault();
    const formData= new FormData();
    formData.append("image",file)
    formData.append("caption",caption)
    // await axios.post('api/posts',formData,{headers:{'Content-Type':'multipart/form-data'}})
    await axios.get('api/posts/all',formData,{headers:{'Content-Type':'multipart/form-data'}})
    // navigate("/")
  }

  const fileSelected=(event)=>{
    const file= event.target.files[0];
    setFile(file)
  }
  return (
    <div>
      <form onSubmit={submit}>
        <input onChange={fileSelected} type="file" accept='image/*'/>
        <input  onChange={e=>setCaption(e.target.value)} value={caption} type="text" placeholder='Caption'/>
        <button type='submit'>Submit</button>
      </form>
    </div>
  )
}

export default NewPost
